mapper.select:
select ab.id, ab.device_id, ab.card_id, ab.content, ab.type, ab.status, ab.time, ab.submit_times
from coin.audit_branch ab
where status = #{cardBranchVerifyWebParam.status}

select d.nick_name
from coin.device d
where d.id = #{deviceId}

select c.title
from coin.card c
where c.id = #{cardId}

update.foreach:
update coin.audit_branch
set status = #{item.status}
where id = #{item.id}

mapper.select:
select ab.id, device_id, card_id, content, type, status, time, submit_times
from coin.audit_branch ab
where ab.id = #{cardBranchVerifyWebParam.id}
